select
count(user_id) from dbuser where mac_address=#{macAddress} and
serial_number=#{serialNum}

SELECT dbcommendtype.commend_type
,dbcommendtype.commend_source,dbcommendtype.field_id,dbcommendtype.commend_screen,dbcommendtype.commend_screen2,dbcommendtype.commend_screen3,dbcommendtype.commend_screen4,dbcommendtype.rank
FROM dbcommendtype ORDER BY rank ASC;

SELECT dbsongs.id,dbsongs.song_name,dbsongs.singer_name,dbsongs.song_format song_model,dbsongs.ordertime,dbsongs.singer_id_one,dbsongs.singer_id_two,dbsongs.singer_id_three FROM dbsongs where lang_id=#{requestParams.field_id}
AND spell like concat(#{requestParams.spell},'%')
AND spell like concat(#{requestParams.spell},'%')
ORDER BY dbsongs.ordertime DESC LIMIT #{requestParams.page},#{requestParams.dataNum}

SELECT count(*) FROM dbsongs where lang_id=#{requestParams.field_id}
AND spell like concat(#{requestParams.spell},'%')
AND spell like concat(#{requestParams.spell},'%')

SELECT dbsongs.id,dbsongs.song_name,dbsongs.singer_name,dbsongs.song_format song_model,dbsongs.ordertime,dbsongs.singer_id_one,dbsongs.singer_id_two,dbsongs.singer_id_three FROM dbsongs where type_id=#{requestParams.field_id}
AND spell like concat(#{requestParams.spell},'%')
AND spell like concat(#{requestParams.spell},'%')
ORDER BY dbsongs.ordertime DESC LIMIT #{requestParams.page},#{requestParams.dataNum}

SELECT count(*) FROM dbsongs where type_id=#{requestParams.field_id}
AND spell like concat(#{requestParams.spell},'%')
AND spell like concat(#{requestParams.spell},'%')

SELECT dbsongs.id,dbsongs.song_name,dbsongs.singer_name,dbsongs.song_format song_model,dbsongs.ordertime,dbsongs.singer_id_one,dbsongs.singer_id_two,dbsongs.singer_id_three FROM dbsongs where theme_id=#{requestParams.field_id} OR theme_id2=#{requestParams.field_id} OR theme_id3=#{requestParams.field_id}
AND spell like concat(#{requestParams.spell},'%')
AND spell like concat(#{requestParams.spell},'%')
ORDER BY dbsongs.ordertime DESC LIMIT #{requestParams.page},#{requestParams.dataNum}

SELECT count(*) FROM dbsongs where theme_id=#{requestParams.field_id} OR theme_id2=#{requestParams.field_id} OR theme_id3=#{requestParams.field_id}
AND spell like concat(#{requestParams.spell},'%')
AND spell like concat(#{requestParams.spell},'%')
